INSERT INTO `his_config_info` VALUES (1, 120, 'application-dev.yml', 'DEFAULT_GROUP', '', '# 加解密根密码\njasypt:\n  encryptor:\n    password: pig #根密码\n\n# Spring 相关\nspring:\n  redis:\n    password: 123456\n    host: 192.168.1.204\n  cloud:\n    sentinel:\n      eager: true\n      transport:\n        dashboard: 192.168.1.204:8080\n  zipkin:\n    base-url: http://192.168.1.204:9411\n  sleuth:\n    sampler:\n      #采样率值介于0到1之间，1则表示全部采集\n      probability: 1\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 20000\n        readTimeout: 20000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n\n#请求处理的超时时间\nribbon:\n  ReadTimeout: 20000\n  ConnectTimeout: 20000\n\n# mybaits-plus配置\nmybatis-plus:\n  mapper-locations: classpath:/mapper/*Mapper.xml\n  global-config:\n    banner: false\n    db-config:\n      id-type: auto\n      table-underline: true\n      logic-delete-value: 1\n      logic-not-delete-value: 0\n  configuration:\n    map-underscore-to-camel-case: true\n\n# spring security 配置\nsecurity:\n  oauth2:\n    resource:\n      loadBalanced: true\n      token-info-uri: http://sh-auth/oauth/check_token\n    # 通用放行URL，服务个性化，请在对应配置文件覆盖\n    ignore:\n      urls:\n        - /**/v2/api-docs\n        - /actuator/**\n# swagger 配置\nswagger:\n  enabled: true\n  title: SmartDeviceCloud Swagger API\n  license: Powered By SmartDeviceCloud\n  licenseUrl: https://netvox.com\n  terms-of-service-url: https://netvox.com\n  contact:\n    email: wangiegie@gmail.com\n    url: https://netvox.com\n  authorization:\n    name: ntx\n    auth-regex: ^.*$\n    authorization-scope-list:\n      - scope: server\n      - description: server all\n    token-url-list:\n\n      - http://${GATEWAY_HOST:sh-gateway}:${GATEWAY-PORT:9999}/auth/oauth/token\